package example.CharOrStrMethods;

public class StringMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "Hello All";
		String str2 = "Hello All";
		String str3 = "hello all  ";
		System.out.println("str.length()=> "+str.length());
		System.out.println("str.charAt(7)=> "+str.charAt(7));
		System.out.println("str.equals(str2)=> "+str.equals(str2));
		System.out.println("str.toLowerCase()=> "+str.toLowerCase());
		System.out.println("str.equals(str3)=> "+str.equals(str3));
		System.out.println("str.equalsIgnoreCase(str3)=> "+str.equalsIgnoreCase(str3));
		System.out.println("str3.trim()=> "+str3.trim());
	}

}
